var express = require('express');
var morgan = require('morgan');
var cors = require('cors');
var path = require('path');
var multer = require('multer');
var app = express();

//Conexion BD
var mongoose = require('mongoose');
var url = 'mongodb://localhost:27017/Rescuepets';
var options = {
  useNewUrlParser: true,
  useUnifiedTopology: true
};
mongoose.connect(url, options).then(function () {
  console.log('Conexion exitosa a mongo');
}, function (err) {
  err;
});

//middleware
app.use(morgan('tiny'));
app.use(cors());
app.use(express.json());
app.use(express.urlencoded({
  extended: true
}));

// Configuraci칩n de Multer
var storage = multer.diskStorage({
  destination: function destination(req, file, cb) {
    cb(null, 'uploads/'); // Carpeta donde se almacenan los archivos
  },

  filename: function filename(req, file, cb) {
    cb(null, Date.now() + '-' + file.originalname); // Nombre del archivo
  }
});

var upload = multer({
  storage: storage
});

// Ruta est치tica para servir archivos desde la carpeta 'uploads'
app.use('/uploads', express["static"](path.join(__dirname, 'uploads')));

//rutas
app.get('/', function (req, res) {
  res.send("hola mundo");
});
app.get('/api/prueba', function (req, res) {
  res.json({
    mensaje: 'Esta es una prueba exitosa.'
  });
});
//middleware para vue.js
app.use('/api', require('./routes/animales'));
app.use('/api', require('./routes/usuarios'));
//app.use('/login', require('./routes/login'));
var history = require('connect-history-api-fallback');
app.use(history());
app.use(express["static"](path.join(__dirname, 'public')));

// Puerto para escuchar 
